</template>

<script>
import {mapGetters} from 'vuex'

export default {
    data() {
        return {
            items: ["Dashboard", "Properties", "Investments", "Transactions"]
        }
    },
    methods: {
        close() {
            this.$store.commit('dashboard/setShowMobileNavigation', false);
        },
        changeTab(tab) {
            this.$store.dispatch('admin/changeTab', tab)
            let url = '';
            
            switch(tab) {
                case "Investments": 
                    url = '/dashboard/investments';
                    break;
                case "Properties": 
                    url = '/dashboard/properties';
                    break;
                case "Transactions": 
                    url = '/dashboard/transactions';
                    break;
                case "Dashboard": 
                    url = '/dashboard';
                    break;
                case "Settings": 
                    url = '/dashboard/settings';
                    break;
                default:
                    url = '/dashboard'
            }
            this.$router.push(url)
        }
    },
    computed: {
        ...mapGetters('dashboard', ['active_tab', 'show_mobile_navigation'])
    }
}

</script>
