        },
        getUsers() {
            this.$api.get(`/admin-users`).then(resp => {
                this.users = resp.data.data;
            })
        },
        computedNavClass(item) {
            if (this.activeTab === item) return "nav-selected"
        },
        changeActiveTab(item) {
            this.activeTab = item;

            if (item === 'users' && !this.users) {
                this.getUsers()
            }
            if (item === 'menu') return;
        },

        addUser() {
            //todo
            this.$api.post('/admin-users', {
                ...this.new_user
            }).then(resp => {
                this.getUsers()
                this.add_user = false;
            })

        }
    }
}

