    data() {
        return {

            pitch: '',


            pitch_hovered: false,
            loading: false,
            pitch_generated: true,

            params: {
                bio: '',
                prof_url: '',
            },
        }
    },
    computed: {
        computedPitch() {
            if (this.pitch) {
                let subject = this.pitch.split("<br>")[0]
                subject = subject.split("Subject:")
                subject = subject.length === 2 ? subject[1] : subject[0]


                let pitch = this.pitch;
                pitch = pitch.replace(subject, '');
                pitch = pitch.replace("Subject:", '');

                return {
                    pitch,
                    subject
                }
            }
        },
        subject() {

        }
    },
    methods: {
        copySubject() {
            var copyText = document.getElementById("subject");

            // Select the text field
            copyText.select();
            copyText.setSelectionRange(0, 99999); // For mobile devices

            // Copy the text inside the text field
            navigator.clipboard.writeText(copyText.value);

            // Alert the copied text
            //alert("Copied the text: " + copyText.value);
        },
        copyPitch() {
            var copyText = document.getElementById("pitch");

            // Select the text field
            copyText.select();
            copyText.setSelectionRange(0, 99999); // For mobile devices

            // Copy the text inside the text field
            navigator.clipboard.writeText(copyText.value);
        },
        validateForm() {
            if (!this.params.bio || this.params.prof_url) {
                this.$toast.error("Please enter your bio and the professor's url")
            }
        },
        getSubject() {
            if (this.pitch && this.pitch.indexOf('Subject') === "Subject") return
        },
        createPitch() {

            this.loading = true;
            document.getElementById("editor").scrollIntoView({ behaviour: "smooth" })
            this.pitch = null;
            this.params = { ...this.params }
            this.$api.post('/generate', this.params).then(resp => {
                this.pitch_generated = true;
                this.pitch = resp.data.data.replace(/\n/g, '<br>');
            }).catch(err => {

                this.$toast.error(err.data.data)
            })
                .finally(() => {
                    this.loading = false;

                })



        },
        copyPitch() {

        }
    }
}
</script>

