<script>
export default {
    data(){
        return {
            payload: {
                bvn: ''
            }
        }
    },
    methods: {
        proceed() {
            if (this.userDetails?.plan) {
                window.open("/dashboard", '_self') 
            }
            else {
                window.open('/onboarding/plan', '_self')
            }
        }
    },
    computed: {
        userDetails() {
            let details = window.localStorage.getItem('userDetails');
            details = details || JSON.parse(details);
            return details;
        }
    }
}
